import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const LoginPage = () => {
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLoginJzuniga = () => {
    login({
      id: 1,
      nombre: 'jzuniga',
      email: 'jzuniga@colegio.com',
      rol: 'profesor'
    });
    navigate('/reservas');
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '2rem'
    }}>
      <div style={{
        background: 'white',
        padding: '3rem',
        borderRadius: '12px',
        boxShadow: '0 10px 30px rgba(0,0,0,0.2)',
        textAlign: 'center',
        maxWidth: '400px',
        width: '100%'
      }}>
        <h1 style={{
          color: '#1976d2',
          marginBottom: '2rem',
          fontSize: '2.5rem',
          fontWeight: 'bold'
        }}>
          Sistema de Reservas
        </h1>
        
        <h2 style={{
          color: '#333',
          marginBottom: '1.5rem',
          fontSize: '1.8rem'
        }}>
          Iniciar Sesión
        </h2>
        
        <p style={{
          color: '#666',
          marginBottom: '2rem',
          fontSize: '1rem',
          lineHeight: '1.5'
        }}>
          Página de login (pendiente de implementar formulario completo).
        </p>

        <button
          onClick={handleLoginJzuniga}
          style={{
            background: '#4caf50',
            color: 'white',
            border: 'none',
            padding: '1rem 2rem',
            borderRadius: '8px',
            fontSize: '1.1rem',
            fontWeight: 'bold',
            cursor: 'pointer',
            transition: 'all 0.3s ease',
            width: '100%',
            marginBottom: '1rem'
          }}
          onMouseEnter={(e) => e.target.style.background = '#45a049'}
          onMouseLeave={(e) => e.target.style.background = '#4caf50'}
        >
          Login como jzuniga
        </button>

        <p style={{
          color: '#999',
          fontSize: '0.9rem',
          fontStyle: 'italic'
        }}>
          Usuario de prueba para demostración
        </p>
      </div>
    </div>
  );
};

export default LoginPage;
